mapper.select:

select * from t_ggpinglun
WHERE 1=1

	 AND ggpinglunDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND ggpinglunDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND ggpinglunDate1 >= #{sdate1,jdbcType=TIMESTAMP} 


	 AND ggpinglunDate1 <= #{edate1,jdbcType=TIMESTAMP} 



		 AND ggpinglunName LIKE  '%${ggpinglun.ggpinglunName}%' 


		 AND ggpinglunId = #{ggpinglun.ggpinglunId} 


		 AND ggtypeId = #{ggpinglun.ggtypeId} 


		 AND gonggaoId = #{ggpinglun.gonggaoId} 


		 AND ggpinglunType = #{ggpinglun.ggpinglunType} 


		 AND ggpinglunType1 = #{ggpinglun.ggpinglunType1} 


		 AND yonghuId = #{ggpinglun.yonghuId} 


		 AND byumenId = #{ggpinglun.byumenId} 


		 AND yhroleId = #{ggpinglun.yhroleId} 


		 AND byuyuanId = #{ggpinglun.byuyuanId} 


		 AND byuzhiId = #{ggpinglun.byuzhiId} 


		 AND userId = #{ggpinglun.userId} 


		 AND bumenId = #{ggpinglun.bumenId} 


		 AND buyuanId = #{ggpinglun.buyuanId} 


		 AND buzhiId = #{ggpinglun.buzhiId} 


		 AND roleId = #{ggpinglun.roleId} 


order by ggpinglunId 

	limit #{page},#{rows}
