mapper.select:

select * from t_gonggao
WHERE 1=1

	 AND gonggaoDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND gonggaoDate <= #{edate,jdbcType=TIMESTAMP} 



		 AND gonggaoName LIKE  '%${gonggao.gonggaoName}%' 


		 AND gonggaoId = #{gonggao.gonggaoId} 


		 AND ggtypeId = #{gonggao.ggtypeId} 


order by gonggaoId 

	limit #{page},#{rows}
