mapper.insert:

insert into t_jcdaohang (jcdaohangId, jcdaohangName, jcdaohangNeirong, 
  jcdaohangMark, jcdaohangMark1, jcdaohangMark2, 
  jcdaohangPaixu, jcdaohangType, jcdaohangType1, 
  jcdaohangType2, jcbiaotiId, jcbiaotiName
  )
values (#{jcdaohangId,jdbcType=INTEGER}, #{jcdaohangName,jdbcType=VARCHAR}, #{jcdaohangNeirong,jdbcType=VARCHAR}, 
  #{jcdaohangMark,jdbcType=VARCHAR}, #{jcdaohangMark1,jdbcType=VARCHAR}, #{jcdaohangMark2,jdbcType=VARCHAR}, 
  #{jcdaohangPaixu,jdbcType=INTEGER}, #{jcdaohangType,jdbcType=INTEGER}, #{jcdaohangType1,jdbcType=INTEGER}, 
  #{jcdaohangType2,jdbcType=INTEGER}, #{jcbiaotiId,jdbcType=INTEGER}, #{jcbiaotiName,jdbcType=VARCHAR}
  )

mapper.select:


select * from t_jcdaohang

WHERE 1=1





		AND jcdaohangName LIKE '%${jcdaohang.jcdaohangName}%'





		AND jcdaohangId = #{jcdaohang.jcdaohangId}





		AND jcbiaotiId = #{jcdaohang.jcbiaotiId}





		AND jcdaohangType = #{jcdaohang.jcdaohangType}





		AND jcdaohangType1 = #{jcdaohang.jcdaohangType1}




order by jcdaohangType, jcbiaotiId, jcdaohangPaixu



	limit #{page},#{rows}
