mapper.select:

select * from t_shuju
WHERE 1=1

	 AND shujuDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND shujuDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND shujuDate1 >= #{sdate1,jdbcType=TIMESTAMP} 


	 AND shujuDate1 <= #{edate1,jdbcType=TIMESTAMP} 



		 AND shujuName LIKE  '%${shuju.shujuName}%' 


		 AND sjleixingName LIKE  '%${shuju.sjleixingName}%' 


		 AND sjxingtaiName LIKE  '%${shuju.sjxingtaiName}%' 


		 AND shujuId = #{shuju.shujuId} 


		 AND sjleixingId = #{shuju.sjleixingId} 


		 AND sjxingtaiId = #{shuju.sjxingtaiId} 


		 AND shujuZong > #{shuju.shujuZong2} 


		 AND shujuType = #{shuju.shujuType} 


		 AND shujuType1 = #{shuju.shujuType1} 


		 AND shujuType2 = #{shuju.shujuType2} 


		 AND yonghuId = #{shuju.yonghuId} 


		 AND byumenId = #{shuju.byumenId} 


		 AND yhroleId = #{shuju.yhroleId} 


		 AND byuyuanId = #{shuju.byuyuanId} 


		 AND byuzhiId = #{shuju.byuzhiId} 


		 AND userId = #{shuju.userId} 


		 AND bumenId = #{shuju.bumenId} 


		 AND buyuanId = #{shuju.buyuanId} 


		 AND buzhiId = #{shuju.buzhiId} 


		 AND roleId = #{shuju.roleId} 


order by shujuId 

	limit #{page},#{rows}
