mapper.select:

select * from t_sjlaiyuan
WHERE 1=1


		AND sjlaiyuanName LIKE '%${sjlaiyuan.sjlaiyuanName}%'


		AND sjlaiyuanPhone LIKE '%${sjlaiyuan.sjlaiyuanPhone}%'


		AND sjlaiyuanId = #{sjlaiyuan.sjlaiyuanId}


		AND sjlaiyuanType = #{sjlaiyuan.sjlaiyuanType}


		AND sjlaiyuanType1 = #{sjlaiyuan.sjlaiyuanType1}


order by sjlaiyuanId

	limit #{page},#{rows}
