mapper.select:

select * from t_sjpinglun
WHERE 1=1

	 AND sjpinglunDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND sjpinglunDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND sjpinglunDate1 >= #{sdate1,jdbcType=TIMESTAMP} 


	 AND sjpinglunDate1 <= #{edate1,jdbcType=TIMESTAMP} 



		 AND sjpinglunName LIKE  '%${sjpinglun.sjpinglunName}%' 


		 AND sjpinglunId = #{sjpinglun.sjpinglunId} 


		 AND sjleixingId = #{sjpinglun.sjleixingId} 


		 AND shujuId = #{sjpinglun.shujuId} 


		 AND sjpinglunType = #{sjpinglun.sjpinglunType} 


		 AND sjpinglunType1 = #{sjpinglun.sjpinglunType1} 


		 AND yonghuId = #{sjpinglun.yonghuId} 


		 AND byumenId = #{sjpinglun.byumenId} 


		 AND yhroleId = #{sjpinglun.yhroleId} 


		 AND byuyuanId = #{sjpinglun.byuyuanId} 


		 AND byuzhiId = #{sjpinglun.byuzhiId} 


		 AND userId = #{sjpinglun.userId} 


		 AND bumenId = #{sjpinglun.bumenId} 


		 AND buyuanId = #{sjpinglun.buyuanId} 


		 AND buzhiId = #{sjpinglun.buzhiId} 


		 AND roleId = #{sjpinglun.roleId} 


order by sjpinglunId 

	limit #{page},#{rows}
