mapper.select:

select * from t_sjqita
WHERE 1=1


		AND sjqitaName LIKE '%${sjqita.sjqitaName}%'


		AND sjqitaPhone LIKE '%${sjqita.sjqitaPhone}%'


		AND sjqitaId = #{sjqita.sjqitaId}


		AND sjqitaType = #{sjqita.sjqitaType}


		AND sjqitaType1 = #{sjqita.sjqitaType1}


order by sjqitaId

	limit #{page},#{rows}
