mapper.select:

select * from t_sjshaochu
WHERE 1=1

	 AND sjshaochuDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND sjshaochuDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND sjshaochuDate1 >= #{sdate1,jdbcType=TIMESTAMP} 


	 AND sjshaochuDate1 <= #{edate1,jdbcType=TIMESTAMP} 



		 AND sjshaochuName LIKE  '%${sjshaochu.sjshaochuName}%' 


		 AND sjshaochuId = #{sjshaochu.sjshaochuId} 


		 AND sjshaochuType = #{sjshaochu.sjshaochuType} 


		 AND sjshaochuType1 = #{sjshaochu.sjshaochuType1} 


		 AND sjshaochuType2 = #{sjshaochu.sjshaochuType2} 


		 AND sjlaiyuanId = #{sjshaochu.sjlaiyuanId} 


		 AND sjqitaId = #{sjshaochu.sjqitaId} 


		 AND shujuId = #{sjshaochu.shujuId} 


		 AND sjleixingId = #{sjshaochu.sjleixingId} 


		 AND sjxingtaiId = #{sjshaochu.sjxingtaiId} 


		 AND yonghuId = #{sjshaochu.yonghuId} 


		 AND byumenId = #{sjshaochu.byumenId} 


		 AND yhroleId = #{sjshaochu.yhroleId} 


		 AND byuyuanId = #{sjshaochu.byuyuanId} 


		 AND byuzhiId = #{sjshaochu.byuzhiId} 


		 AND userId = #{sjshaochu.userId} 


		 AND bumenId = #{sjshaochu.bumenId} 


		 AND buyuanId = #{sjshaochu.buyuanId} 


		 AND buzhiId = #{sjshaochu.buzhiId} 


		 AND roleId = #{sjshaochu.roleId} 


order by sjshaochuId 

	limit #{page},#{rows}
