mapper.select:

select * from t_sjxingtai
WHERE 1=1


		AND sjxingtaiName LIKE '%${sjxingtai.sjxingtaiName}%'


		AND sjxingtaiPhone LIKE '%${sjxingtai.sjxingtaiPhone}%'


		AND sjxingtaiId = #{sjxingtai.sjxingtaiId}


		AND sjxingtaiType = #{sjxingtai.sjxingtaiType}


		AND sjxingtaiType1 = #{sjxingtai.sjxingtaiType1}


order by sjxingtaiId

	limit #{page},#{rows}
