mapper.select:

select * from t_user
WHERE 1=1

	 AND userName = #{userName,jdbcType=VARCHAR} 


	 AND userDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND userDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND userDate1 >= #{sdate,jdbcType=TIMESTAMP} 


	 AND userDate1 <= #{edate,jdbcType=TIMESTAMP} 



		 AND userXingming LIKE  '%${user.userXingming}%' 


		 AND userName LIKE  '%${user.userName}%' 


		 AND userPassword = #{user.userPassword} 


		 AND userMinzu = #{user.userMinzu} 


		 AND userId = #{user.userId} 


		 AND userType = #{user.userType} 


		 AND userType1 = #{user.userType1} 


		 AND userType2 = #{user.userType2} 


		 AND roleId = #{user.roleId}


		 AND userSex = #{user.userSex}


		 AND buyuanId = #{user.buyuanId}


		 AND bumenId = #{user.bumenId}


		 AND buzhiId = #{user.buzhiId}


order by userId 

	limit #{page},#{rows}
