mapper.select:

select * from t_uyijian
WHERE 1=1

	 AND uyijianDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND uyijianDate <= #{edate,jdbcType=TIMESTAMP} 



		 AND uyijianName LIKE  '%${uyijian.uyijianName}%' 


		 AND uyijianId = #{uyijian.uyijianId} 


		 AND uxtypeId = #{uyijian.uxtypeId} 


		 AND uyijianType = #{uyijian.uyijianType} 


		 AND uyijianType1 = #{uyijian.uyijianType1} 


		 AND userId = #{uyijian.userId} 


		 AND bumenId = #{uyijian.bumenId} 


		 AND buyuanId = #{uyijian.buyuanId} 


		 AND buzhiId = #{uyijian.buzhiId} 


		 AND roleId = #{uyijian.roleId} 


order by uyijianId 

	limit #{page},#{rows}
