mapper.select:

select * from t_yonghu
WHERE 1=1

	 AND yonghuName = #{yonghuName,jdbcType=VARCHAR} 


	 AND yonghuDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND yonghuDate <= #{edate,jdbcType=TIMESTAMP} 


	 AND yonghuDate1 >= #{sdate1,jdbcType=TIMESTAMP} 


	 AND yonghuDate1 <= #{edate1,jdbcType=TIMESTAMP} 



		 AND yonghuName LIKE  '%${yonghu.yonghuName}%' 


		 AND yonghuPassword = #{yonghu.yonghuPassword} 


		 AND yonghuMinzu = #{yonghu.yonghuMinzu} 


		 AND yonghuId = #{yonghu.yonghuId} 


		 AND yonghuType = #{yonghu.yonghuType} 


		 AND yonghuType1 = #{yonghu.yonghuType1} 


		 AND yonghuType2 = #{yonghu.yonghuType2} 


		 AND yonghuSex = #{yonghu.yonghuSex} 


		 AND byumenId = #{yonghu.byumenId} 


		 AND yhroleId = #{yonghu.yhroleId} 


		 AND byuyuanId = #{yonghu.byuyuanId} 


		 AND byuzhiId = #{yonghu.byuzhiId} 


		 AND userId = #{yonghu.userId} 


		 AND bumenId = #{yonghu.bumenId} 


		 AND buyuanId = #{yonghu.buyuanId} 


		 AND buzhiId = #{yonghu.buzhiId} 


order by yonghuId 

	limit #{page},#{rows}
